http('\n');
http('<option id="q1" value=\'SELECT ?name, ?surname WHERE {\n');
http('  ?person a <http://dbpedia.org/ontology/Person> . \n');
http('  ?person   <http://it.dbpedia.org/property/nome>    ?name .\n');
